import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate(); // Initialize navigation

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5050/login", { email, password });
      localStorage.setItem("token", res.data.token); // Save token in local storage
      setMessage("Login successful!");

      setTimeout(() => {
        navigate("/send-email"); // Redirect to Send Email page
      }, 1000); // Delay for user experience
    } catch (err) {
      setMessage(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div>
      <h2>Login with mail.psn.co.id</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Your email (mail.psn.co.id)"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default Login;
